import React from 'react';
import {
  Box,
  Container,
  Typography,
  Paper,
} from '@mui/material';
import {
  Timeline,
  TimelineItem,
  TimelineSeparator,
  TimelineConnector,
  TimelineContent,
  TimelineDot,
} from '@mui/lab';
import { motion } from 'framer-motion';
import WorkIcon from '@mui/icons-material/Work';
import SchoolIcon from '@mui/icons-material/School';

const experiences = [
  {
    title: 'Your Job Title',
    company: 'Your Company',
    period: 'Your Period',
    description: [
      'Your achievement or responsibility 1',
      'Your achievement or responsibility 2',
      'Your achievement or responsibility 3',
    ],
    type: 'work',
  },
  {
    title: 'Your Education Title',
    company: 'Your University',
    period: 'Your Education Period',
    description: [
      'Your educational achievement 1',
      'Your educational achievement 2',
      'Your educational achievement 3',
    ],
    type: 'education',
  },
];

const Experience = () => {
  return (
    <Box sx={{ py: 8 }}>
      <Container maxWidth="lg">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <Typography variant="h3" component="h1" gutterBottom align="center">
            Experience
          </Typography>
          <Typography
            variant="body1"
            paragraph
            align="center"
            sx={{ maxWidth: 800, mx: 'auto', mb: 6 }}
          >
            My professional journey and educational background have shaped me into
            the developer I am today.
          </Typography>

          <Timeline position="alternate">
            {experiences.map((exp, index) => (
              <TimelineItem key={index}>
                <TimelineSeparator>
                  <TimelineDot color="primary">
                    {exp.type === 'work' ? <WorkIcon /> : <SchoolIcon />}
                  </TimelineDot>
                  {index < experiences.length - 1 && <TimelineConnector />}
                </TimelineSeparator>
                <TimelineContent>
                  <Paper
                    elevation={3}
                    sx={{ p: 3, mb: 2 }}
                    component={motion.div}
                    whileHover={{ scale: 1.02 }}
                  >
                    <Typography variant="h6" component="h3" gutterBottom>
                      {exp.title}
                    </Typography>
                    <Typography
                      variant="subtitle1"
                      color="primary"
                      gutterBottom
                    >
                      {exp.company}
                    </Typography>
                    <Typography
                      variant="body2"
                      color="text.secondary"
                      gutterBottom
                    >
                      {exp.period}
                    </Typography>
                    <ul style={{ margin: 0, paddingLeft: '1.2rem' }}>
                      {exp.description.map((item, i) => (
                        <li key={i}>
                          <Typography variant="body1">{item}</Typography>
                        </li>
                      ))}
                    </ul>
                  </Paper>
                </TimelineContent>
              </TimelineItem>
            ))}
          </Timeline>
        </motion.div>
      </Container>
    </Box>
  );
};

export default Experience;
